mapper.select:
SELECT *, u.username, u.alias, u.avatar FROM bms_codepost t LEFT JOIN ums_user u ON t.user_id = u.id
and u.username = #{username}
order by t.view desc, t.create_time desc

SELECT *, u.username, u.alias, u.avatar FROM bms_codepost t LEFT JOIN ums_user u ON t.user_id = u.id
and t.title like CONCAT('%','${keyword}') or t.title like CONCAT('${keyword}','%') or t.title like CONCAT('%','${keyword}','%') or t.title = #{keyword} or t.description like CONCAT('${keyword}','%') or t.description like CONCAT('%','${keyword}','%') or t.description = #{keyword}
order by t.view desc, t.create_time desc

SELECT * FROM bms_codepost t where
original_post_id = #{codePostId}
order by t.create_time desc
